import React from "react";
import Axios from "axios";

class Result extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      submit: false,
      items: [],
      isLoaded: false,
      city: undefined,
      state: undefined
    };
    this.handleChange = this.handleChange.bind(this);
    this.submit = this.submit.bind(this);
  }
  componentDidMount() {
    Axios.get(
      "https://raw.githubusercontent.com/adrian-stru/travel-filters/master/DATA/json/attractions.json?token=ARfVeQkcPKWc6HCyHfODTXWdxAkqGqebks5cCgdfwA%3D%3D"
    )
      .then(response =>
        response.data.results.map(item => ({
          city: "{item.city}",
          state: "{item.state}"
          //image:
        }))
      )
      .then(item => {
        this.setState({
          isLoading: false
        });
      })
      .catch(error => this.setState({ error, isLoading: false }));
  }
  handleChange(e) {
    this.setState({
      [e.target.name]: e.target.value
    });
  }
  onClickSubmit(e) {
    this.setState({ submit: true });
  }

  submit(e) {
    e.preventDefault();
    this.Auth.login(this.state.email, this.state.password)
      .then(res => {
        this.props.history.replace("/ResultList");
      })
      .catch(err => {
        console.log(err);
        this.setState({ hasError: true });
      });
  }
  render() {
    var { isLoaded, items } = this.state;
    if (!isLoaded) {
      return <div>loading..</div>;
    } else {
      return (
        <React.Fragment>
          <h1>State</h1>
          <div>
            <ol>
              {/* get the asked city Name */}
              {items.map(item => (
                <li key={item.state}>City: {item.city}</li>
              ))}
              {/* get city picture */}
            </ol>
          </div>
          <div className="ResultList-button">
            <input
              className="button"
              type="submit"
              value="Submit"
              onClick={e => this.onClickSubmit(e)} //change to onSubmit when ready
            />
          </div>
        </React.Fragment>
      );
    }
  }
}

export default Result;
