import React, { Component } from "react";
import "../Login/Login.css";
import AuthService from "../AuthService/AuthService";

class Register extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "",
      password: "",
      reenterPass: "",
      hasError: false
    };
    this.handleChange = this.handleChange.bind(this);
    this.submit = this.submit.bind(this);
    this.Auth = new AuthService();
  }

  render() {
    let submit;
    let errorMessage;

    if (this.state.password !== this.state.reenterPass) {
      submit = (
        <div>
          <input className="button" type="submit" value="Register" disabled />
          <p style={{ color: "red", fontSize: "15px" }}>
            Passwords do not match
          </p>
        </div>
      );
    } else {
      submit = <input className="button" type="submit" value="Register" />;
    }
    if (this.state.hasError) {
      errorMessage = (
        <p style={{ color: "red", fontSize: "15px" }}>
          Error in registration. Try again.
        </p>
      );
    }
    return (
      <div>
        <h3> Register </h3>

        <form onSubmit={e => this.submit(e)}>
          <div className="input-box">
            <input
              type="email"
              name="email"
              placeholder="Email"
              size="25"
              required
            />
            <br />
          </div>
          <div className="input-box">
            <input
              type="password"
              name="password"
              placeholder="Password"
              size="25"
              required
              onChange={e => this.handleChange(e)}
            />
            <br />
          </div>
          <div className="input-box">
            <input
              type="password"
              name="reenterPass"
              placeholder="Re-enter Password"
              size="25"
              required
              onChange={e => this.handleChange(e)}
            />
            <br />
          </div>
          {submit}
          {errorMessage}
          <br />
        </form>
      </div>
    );
  }

  handleChange(e) {
    this.setState({
      [e.target.name]: e.target.value
    });
  }

  submit(e) {
    e.preventDefault();
    this.Auth.login(this.state.email, this.state.password)
      .then(res => {
        this.props.history.replace("/Survey");
      })
      .catch(err => {
        console.log(err);
        this.setState({ hasError: true });
      });
  }
}
export default Register;
